import React, { useEffect, useState } from 'react';
import axios from 'axios';
import TaskList from './components/TaskList'; // Adjust the path as per your file structure
import TaskForm from './components/TaskForm'; // Make sure this path is correct
import Header from './components/Header'; // Import Header component
import Footer from './components/Footer'; // Import Footer component
import './App.css'; // Ensure this CSS file is created

const App = () => {
  const [tasks, setTasks] = useState([]);
  const [selectedTask, setSelectedTask] = useState(null);
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const response = await axios.get('http://localhost:5000/tasks'); // Adjust API endpoint as per your backend setup
      setTasks(response.data); // Assuming response.data is an array of tasks
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  const handleDeleteTask = async (taskId) => {
    try {
      await axios.delete(`http://localhost:5000/tasks/${taskId}`);
      fetchTasks(); // Refresh task list after deletion
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  const handleSaveTask = async (task) => {
    try {
      if (isEditing && selectedTask) {
        await axios.put(`http://localhost:5000/tasks/${selectedTask._id}`, task);
      } else {
        await axios.post('http://localhost:5000/tasks', task);
      }
      setIsEditing(false);
      setSelectedTask(null);
      fetchTasks();
    } catch (error) {
      console.error('Error saving task:', error);
    }
  };

  const handleEditTask = (task) => {
    setSelectedTask(task);
    setIsEditing(true);
  };

  return (
    <div className="App">
      <Header />
      <main>
        <TaskForm onSave={handleSaveTask} taskToEdit={selectedTask} />
        <TaskList tasks={tasks} onDelete={handleDeleteTask} onUpdate={handleEditTask} />
      </main>
      <Footer />
    </div>
  );
};

export default App;
